import React from 'react';
import axios from 'axios';

class CurrentPost extends React.Component {

    constructor(props) {
        super(props);

        this.editPost = React.createRef();
        this.editName = React.createRef();

        this.state = {
            newdata: [],
            random: null,
            loading: true,
            editName: null,
            editPost: null,
        };
    }


    componentDidMount() {
        this.updateData();
        this.setState({ loading: false })
    }

    updateData() {

        axios.get('http://localhost:5000/getdata')
        .then((response) => {
            this.setState({ newdata: response.data })
        })
        .then((response) => {
            let x = 0;
            this.state.newdata.map((data) => {
                data.id = x;
                data.edit = false;
                console.log(data.id)
                x++;

                this.forceUpdate();
            })
        })
        .then((response) => {
            this.forceUpdate();
        })
        .catch(function (error) {
            console.log(error);
        });

    }

    changeEdit(param) {
        this.state.newdata.map((data) => data.edit = false)
        this.state.newdata[param].edit = true;
        this.forceUpdate();
    }

    handleEdit(p1) {
        let editData = { id: p1, name: this.state.editName, post: this.state.editPost  };

        console.log('doing edit')
        axios.post('http://localhost:5000/editdata', editData)
            .then(res => {
                console.log('testing')
               this.updateData();
            })
            this.state.newdata[p1].edit = false;

    }

    handleDelete(param) {
        console.log('You wanted to delete: ' + param);
        let deletePost = { id: param };
        axios.post('http://localhost:5000/deldata', deletePost)
            .then(res => {
                this.updateData();
            })
    }


    render() {
        const cardStyle = {
            width: '23%',
            minWidth: '300px',
            height: '15rem',
            float: 'left',
            margin: '10px'
        };

        const updateName = (evt) => {
            this.setState({ editName: evt.target.value });

        };

        const updatePost = (evt) => {
            this.setState({ editPost: evt.target.value });
        };

        const cards = this.state.newdata.map((val) => {
            if (val.edit === true) {
                return (
                    <div key={val.id} className="card bg-light mb-3" style={cardStyle}>
                        <div className="card-header">
                            <div className="row">
                                <div className="col-3 text-left">
                                    <input type="submit" className="btn btn-primary" value="Update" onClick={() => { this.handleEdit(val.id) }} />
                                </div>
                                <div className="col-8 text-center my-auto">
                                    <input type="text" ref={this.editName} className="form-control" placeholder="Your name here" defaultValue={val.name} onChange={updateName(this.editPost)} name="myname" />
                                </div>

                            </div>
                        </div>
                        <div className="card-body">
                            <input type="text" ref={this.editPost} className="form-control" placeholder="Your message here" defaultValue={val.post} onChange={updatePost} name="mypost" />
                        </div>
                    </div>
                )
            } else {
                return (
                    <div key={val.id} className="card bg-light mb-3" style={cardStyle} >
                        <div className="card-header">
                            <div className="row">
                                <div className="col-3 text-left">
                                    <ul className="nav nav-pills">
                                        <li className="nav-item dropdown">
                                            <a className="nav-link dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Action</a>
                                            <div className="dropdown-menu" x-placement="bottom-start">
                                                <a className="dropdown-item" onClick={() => { this.handleDelete(val.id) }}>Delete</a>
                                                <a className="dropdown-item" onClick={() => { this.changeEdit(val.id) }} >Edit</a>
                                            </div>
                                        </li>
                                    </ul>
                                </div>
                                <div className="col-6 text-center my-auto">
                                    <h5>{val.name}</h5>
                                </div>
                                <div className="col-3 text-right">

                                </div>
                            </div>
                        </div>
                        <div className="card-body">
                            <p className="card-text">{val.post}</p>
                        </div>
                    </div>
                )
            }
        })


        if (this.state.loading === false) {
            return (
                <div className="col-sm text-center">
                    <h1>Current Posts:</h1>
                    <div className="text-center">
                        {cards}
                    </div>
                </div>
            )
        } else {
            return (
                <p>Loading..</p>
            )
        }

    }

}

export default CurrentPost;
