import React from 'react';
import axios from 'axios';

class CurrentPost extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            newdata: [],
            random: null,
        };
    }


    componentDidMount() {
        // Call our fetch function below once the component mounts
        axios
            .get('http://localhost:5000/getdata')
            .then((response) => {
                this.setState({ newdata: response.data })
            })
            .then ((response) => {
                let x = 0;
                this.state.newdata.map((data) => {
                    data.id = x;
                    x++;
                })
            })
            .catch(function (error) {
                console.log(error);
            });

    }

    handleEdit(param) {
       this.state.newdata[param].edit = true;
       console.log(this.state.newdata);
    }

    handleDelete(param) {
        console.log('You wanted to delete: ' + param);

        let deletePost = { id: param};

        axios.post('http://localhost:5000/deldata', deletePost)
        .then(res => {
            axios.get('http://localhost:5000/getdata')
            .then((response) => {
                this.setState({ newdata: response.data })
            })
            .then ((response) => {
                let x = 0;
                this.state.newdata.map((data) => {
                    data.id = x;
                    data.edit = false;
                    x++;
                })
            })
            .catch(function (error) {
                console.log(error);
            });
        })
    }


    render() {
        const cardStyle = {
            width: '23%',
            minWidth: '300px',
            height: '15rem',
            float: 'left',
            margin: '10px'
        };


        const cards = this.state.newdata.map((val) => {
            if (val.edit === true) {
                return (
                    <h1>edit</h1>
                )
            } else {
                return (
                <div className="card bg-light mb-3" style={cardStyle} key={val.id}>
                    <div className="card-header">
                        <div className="row">
                            <div className="col-3 text-left">
                            <ul className="nav nav-pills">
                                    <li className="nav-item dropdown">
                                        <a className="nav-link dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Action</a>
                                        <div className="dropdown-menu" x-placement="bottom-start">
                                            <a className="dropdown-item" onClick={() => { this.handleDelete(val.id) }}>Delete</a>
                                            <a className="dropdown-item" onClick={() => { this.handleEdit(val.id) }} >Edit</a>
                                        </div>
                                    </li>   
                                </ul>
                            </div>
                            <div className="col-6 text-center my-auto">
                                <h5>{val.name}</h5>
                            </div>
                            <div className="col-3 text-right">

                            </div>
                        </div>
                    </div>
                    <div className="card-body">
                        <p className="card-text">{val.post}</p>
                    </div>
                </div>
                )
            }
        })



        return (
            <div className="col-sm text-center">
                <h1>Current Posts:</h1>

            <div className="text-center">
                {cards}
            </div>


            </div>
        )

    }

}

export default CurrentPost;
