import React from 'react';
import axios from 'axios';

class CurrentPost extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            newdata: [],
        };
    }


    componentDidMount() {
        // Call our fetch function below once the component mounts
        axios
            .get('http://localhost:5000/getdata')
            .then((response) => {
                console.log(response.data);
                this.setState({ newdata: response.data })
            })
            .catch(function (error) {
                console.log(error);
            });

    }


    render() {
        let x = -1;

        const cardStyle = {
            width: '23%',
            height: '15rem',
            float: 'left',
            margin: '10px'
        };


        const cards = this.state.newdata.map((val) =>
            <div className="card bg-light mb-3" style={cardStyle} key={x++}>
                <div className="card-header">
                    <div className="row">
                        <div className="col-3 text-left">
                        <ul class="nav nav-pills">
                                <li class="nav-item dropdown">
                                    <a class="nav-link dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Dropdown</a>
                                    <div class="dropdown-menu" x-placement="bottom-start">
                                        <a class="dropdown-item" href="#">Action</a>
                                        <a class="dropdown-item" href="#">Another action</a>
                                        <a class="dropdown-item" href="#">Something else here</a>
                                        <div class="dropdown-divider"></div>
                                        <a class="dropdown-item" href="#">Separated link</a>
                                    </div>
                                </li>   
                            </ul>
                        </div>
                        <div className="col-6 text-center">
                            <p>{val.name}</p>
                        </div>
                        <div className="col-3 text-right">

                        </div>
                    </div>
                </div>
                <div className="card-body">
                    <p className="card-text">{val.post}</p>
                </div>
            </div>
        )



        return (
            <div className="col-sm text-center">
                <h1>Current Posts:</h1>


                {cards}



            </div>
        )

    }

}

export default CurrentPost;
