import React from 'react';
import axios from 'axios';

class CurrentPost extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            newdata: [],
        };
    }


    componentDidMount() {
        // Call our fetch function below once the component mounts
        axios
            .get('http://localhost:5000/getdata')
            .then((response) => {
                console.log(response.data);
                this.setState({ newdata: response.data })
            })
            .catch(function (error) {
                console.log(error);
            });

    }


    render() {
        let x = -1;

        const cardStyle = {
            width: '23%',
            height: '15rem',
            float: 'left',
            margin: '10px'
        };


        const cards = this.state.newdata.map((val) =>
            <div className="card bg-light mb-3" style={cardStyle} key={x++}>
                <div className="card-header">
                    <div className="row">
                        <div className="col-3 text-left">
                            <form method="post" action="http://localhost:5000/deldata">
                                <input type="submit" value="Delete" className="btn btn-danger" />
                            </form>
                        </div>
                        <div className="col-6 text-center">
                            <h5>{val.name}</h5>
                        </div>
                        <div className="col-3 text-right">
                            <button type="button" class="btn btn-success">Edit</button>
                        </div>
                    </div>
                </div>
                <div className="card-body">
                    <p className="card-text">{val.post}</p>
                </div>
            </div>
        )



        return (
            <div className="col-sm text-center">
                <h1>Current Posts:</h1>


                {cards}



            </div>
        )

    }

}

export default CurrentPost;
